    public function getSourceContext()
    {
        return new Source("{% extends 'Backend/_layout.html.twig' %}

{% block page_title %}
    <h3 class=\"page-title\"> Focus Group
        <small>édition d'un message</small>
    </h3>
{% endblock %}

{% block content %}
    <div class=\"portlet light bordered\">
        <div class=\"portlet-title\">
            <div class=\"caption\">
                <span class=\"caption-subject font-red-sunglo bold uppercase\">Edition</span>
            </div>
        </div>
        <div class=\"portlet-body form\">
            {{ form_start(edit_form, {'attr' : {
                'class' : 'form-horizontal'
            }}) }}
            <div class=\"form-body\">
                <div class=\"form-group\">
                    {{ form_label(edit_form.content, null, {'label': 'Message', 'label_attr': {'class': 'col-md-2 control-label'}}) }}
                    <div class=\"col-md-10\">
                        {{ form_widget(edit_form.content, {'attr': {'class': 'form-control', 'placeholder' : 'Message'}}) }}
                    </div>
                </div>
            </div>
            <div class=\"form-actions\">
                <div class=\"row\">
                    <div class=\"col-md-offset-3 col-md-4\">
                        <button class=\"btn green\" type=\"submit\">Enregistrer</button>
                        <a class=\"btn default\" href=\"{{ path('admin_focusgroup_show', {id: focusMessage.focusGroup.id}) }}\">Retour</a>
                    </div>
                </div>
            </div>
            {{ form_end(edit_form) }}
        </div>
    </div>
{% endblock %}
", "Backend/FocusGroup/edit_message.html.twig", "/home/ygxopek/www/dd.club.syres.fr/templates/Backend/FocusGroup/edit_message.html.twig");
